import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobList = () => {
    const [jobs, setJobs] = useState([]);
    const [searchTitle, setSearchTitle] = useState('');
    const [searchCity, setSearchCity] = useState('');
    const [filters, setFilters] = useState({ country: "", city: "", town: "", workPref: "" });

    // Fetch jobs based on search criteria or filters
    const fetchJobs = () => {
        axios.get(`http://localhost:5000/api/jobs?title=${searchTitle}&location=${searchCity}`)
            .then(response => setJobs(response.data))
            .catch(error => console.error('Error fetching jobs:', error));
    };

    // Apply additional filters
    const applyFilters = () => {
        axios.get(`http://localhost:5000/api/jobs?country=${filters.country}&city=${filters.city}`)
            .then(response => setJobs(response.data))
            .catch(error => console.error('Error:', error));
    };

    // Fetch jobs on component mount
    useEffect(() => {
        fetchJobs();
    }, [searchTitle, searchCity]);

    return (
        <div>
            <h1>Job Listings</h1>

            {/* 🔎 Search Bar */}
            <div style={{ marginBottom: "20px" }}>
                <input 
                    type="text" 
                    placeholder="Search by job title..." 
                    value={searchTitle} 
                    onChange={(e) => setSearchTitle(e.target.value)}
                    style={{ marginRight: "10px", padding: "8px" }}
                />
                <input 
                    type="text" 
                    placeholder="Search by city..." 
                    value={searchCity} 
                    onChange={(e) => setSearchCity(e.target.value)}
                    style={{ padding: "8px" }}
                />
                <button onClick={fetchJobs} style={{ marginLeft: "10px", padding: "8px" }}>Search</button>
            </div>

            {/* 🏷️ Filters Section */}
            <div style={{ marginBottom: "20px" }}>
                <h3>Filters</h3>
                <input 
                    type="text" 
                    placeholder="City" 
                    onChange={(e) => setFilters({ ...filters, city: e.target.value })} 
                    style={{ marginRight: "10px", padding: "8px" }}
                />
                <input 
                    type="text" 
                    placeholder="Country" 
                    onChange={(e) => setFilters({ ...filters, country: e.target.value })} 
                    style={{ padding: "8px" }}
                />
                <button onClick={applyFilters} style={{ marginLeft: "10px", padding: "8px" }}>Apply Filters</button>
            </div>

            {/* 🔹 Job List */}
            <ul>
                {jobs.length > 0 ? (
                    jobs.map(job => (
                        <li key={job.id} style={{ marginBottom: "20px", padding: "10px", border: "1px solid #ddd", borderRadius: "5px" }}>
                            <h3>{job.title}</h3>
                            <p>{job.description}</p>
                            <p><strong>Location:</strong> {job.location}</p>
                            <p><strong>Company:</strong> {job.company}</p>
                            <p><strong>Salary:</strong> {job.salary}</p>
                        </li>
                    ))
                ) : (
                    <p>No jobs found.</p>
                )}
            </ul>
        </div>
    );
};

export default JobList;
